import React from 'react';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clubs: [
        {
          id: 1,
          name: "Barcelona",
          point: 69,
        },
        {
          id: 2,
          name: "Real Madrid",
          point: 56,
        },
        {
          id: 3,
          name: "Atlético Madrid",
          point: 66,
        },
      ],
    };
  }

  setNewData = () => {
    let id = 3;
    this.setState(prevState => ({
      clubs: prevState.clubs.map(
        club => club.id === id ? {...club, point: 72} : club
      )
    }))
  }

  render() {
    return (
      <div>
        <button onClick={this.setNewData}>Change New Data</button>
        {this.state.clubs.map((team) => (
          <div>
            <h1>{team.name}</h1>
            <h1>{team.point}</h1>
          </div>
        ))}
      </div>
    );
  }
}
export default App;
