import React from 'react';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: 0
    }
  }

  setNewNumber = () => {
    this.setState((prevState) => {
      return {
        data: prevState + 1
      }
    })
  }

  render() {
    return(
      <div>
        <button onClick={this.setNewNumber}>INCREMENT</button>
        <h1>{this.state.data}</h1>
      </div>
    )
  }
}
export default App;
