import React from 'react';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user : {
        name: 'Duong Bao Hung',
        age: '20',
        job: 'Developer',
        address: 'Ho Chi Minh',
        lover: 'Ho My Nghi'
      }
    }
  }

  setNewData = () => {
    this.setState(prevState => {
      let user = {...prevState.user};
      user.address = 'Quang Nam';
      user.age = 30;
      return { user };
    });
  }

  render() {
    return(
      <div>
        <button onClick={this.setNewData}>Change New Data</button>
        <h1>{this.state.user.name}</h1>
        <h1>{this.state.user.age}</h1>
        <h1>{this.state.user.lover}</h1>
        <h1>{this.state.user.address}</h1>
        <h1>{this.state.user.job}</h1>
      </div>
    )
  }
}
export default App;
